function posepsd8!(model, cons, tsupp, L; lattice="chain")
    ltsupp = length(tsupp)
    Pauli = Matrix{Complex{Int8}}[[1 0; 0 1], [0 1; 1 0], [0 -im; im 0], [1 0; 0 -1]]
    blocks = [[1], [2, 3, 5, 9, 17, 33, 65, 129], [4, 6, 7, 10, 11, 13, 18, 19, 21, 25, 34, 35, 37, 41, 49, 66, 67, 69, 73, 81, 97, 130, 131, 133, 137, 145, 161, 193], [8, 12, 14, 15, 20, 22, 23, 26, 27, 29, 36, 38, 39, 42, 43, 45, 50, 51, 53, 57, 68, 70, 71, 74, 75, 77, 82, 83, 85, 89, 98, 99, 101, 105, 113, 132, 134, 135, 138, 139, 141, 146, 147, 149, 153, 162, 163, 165, 169, 177, 194, 195, 197, 201, 209, 225], [16, 24, 28, 30, 31, 40, 44, 46, 47, 52, 54, 55, 58, 59, 61, 72, 76, 78, 79, 84, 86, 87, 90, 91, 93, 100, 102, 103, 106, 107, 109, 114, 115, 117, 121, 136, 140, 142, 143, 148, 150, 151, 154, 155, 157, 164, 166, 167, 170, 171, 173, 178, 179, 181, 185, 196, 198, 199, 202, 203, 205, 210, 211, 213, 217, 226, 227, 229, 233, 241]]
    pos = [@variable(model, [1:length(block), 1:length(block)], PSD) for block in blocks]
    for i = 0:3, j = 0:3, k = 0:3, l = 0:3, s = 0:3, t = 0:3, u = 0:3, v = 0:3
        ind = [i,j,k,l,s,t,u,v]
        if all(x->iseven(sum(ind .== x)), 1:3)
            inx = ind .!= 0
            Locb = bfind(tsupp, ltsupp, reduce4(UInt16.(3*(Vector(1:8)[inx] .- 1) + ind[inx]), L, lattice=lattice))
            tp = real(kron(Pauli[ind.+1]...))
            for (p, block) in enumerate(blocks)
                @inbounds add_to_expression!(cons[Locb], sum(tp[block, block].*pos[p]))
            end
        end
    end
end

